    //invitetop
    var inviteData = Actions.invitePData;

    async function main() {
        var sortedItems = "None";
        if (inviteData["inviterData"].hasOwnProperty(msg.guild.id)) {
            var guildData = inviteData["inviterData"][msg.guild.id];
            var inviteArr = [];

            var objectKeys = Object.keys(guildData);

            if (objectKeys.length > 0) {
                sortedItems = "";
                for (i = 0; i < objectKeys.length; i++) {
                    var keyIndex = objectKeys[i];
                    guildData[keyIndex]["id"] = keyIndex;
                    inviteArr.push(guildData[keyIndex]);
                }

                var sortedArr = inviteArr.sort(function(a, b) {
                    return b["data"]["real"] - a["data"]["real"]
                });

                if (sortedArr.length >= 10) {
                    for (i = 0; i < 10; i++) {
                        var clientUser = await client.users.fetch(sortedArr[i]["id"]);
                        sortedItems += `${clientUser} - ${sortedArr[i]["data"]["real"]} Real | ${sortedArr[i]["data"]["invalid"]} Invalid\n`
                    }
                } else {
                    for (i = 0; i < sortedArr.length; i++) {
                        var clientUser = await client.users.fetch(sortedArr[i]["id"]);
                        sortedItems += `${clientUser} - ${sortedArr[i]["data"]["real"]} Real | ${sortedArr[i]["data"]["invalid"]} Invalid\n`
                    }
                }
            }
        }

        msg.channel.send({
            embed: {
                color: 0x0099ff,
                title: `ðŸ“ˆ Invite Leaderboard (${msg.guild.name})`,
                description: `ðŸ‘¥ The top inviters are..\n\n${sortedItems}`,
                thumbnail: {
                    url: msg.guild.iconURL(),
                },
                timestamp: new Date(),

            }
        });

    }

    main();

